import React, {useState, useEffect} from 'react';
import firebase from 'firebase/app';
import 'firebase/database';
import './estilos.css'
// import { getDatabase, ref, onValue} from "firebase/database";





const Contador = () => {
    const [puntoFinal, cambiarPuntoFinal] = useState({});

    const Config = {
        apiKey: "AIzaSyBgwpoqanque0kR0_S-S6qumKPeEB_wS1w",
        authDomain: "punteador-9cd23.firebaseapp.com",
        databaseURL: "https://punteador-9cd23-default-rtdb.firebaseio.com",
        projectId: "punteador-9cd23",
        storageBucket: "punteador-9cd23.appspot.com",
        messagingSenderId: "73532947608",
        appId: "1:73532947608:web:a14c4007b337aa5c3b64ed"
    };

    if(!firebase.apps.length){
        firebase.initializeApp(Config)
    }

    useEffect(() => {

        firebase.database().ref().on('value', (puntos) => {
            puntos.forEach((punto) => {
                cambiarPuntoFinal(punto.val())
                // console.log(obj)
            })

            // const data = puntos.val();
            // updateStarCount(postElement, data);
            // console.log(data)
            // console.log(arr)
            // return(() => {
                //     console.log(punto.value)
                // })
            })

        // const db = firebase.Database();
        // const starCountRef = db.ref('PUNTUADOR_FINAL_SAN_REMO_ULTIMATE')
        // onValue(starCountRef, (snapshot) => {
        // const data = snapshot.val();
        // updateStarCount(postElement, data);
// });
    }, []);
    const puntuacion = puntoFinal.PUNTUACION.replace(/['"]+/g, '')
    const pareja1 = puntoFinal.PAREJA1.replace(/['"]+/g, '')

    return (
        <div>
            {/* <h1>{this.data.PUNTUADOR_FINAL_SAN_REMO_ULTIMATE}</h1> */}
            <table className="tabla-puntos">
                <thead>
                    <tr>
                        <th></th>
                        <th></th>
                        <th></th>
                        {puntoFinal.P12S !== '""' || puntoFinal.P22S !== '""' ?
                            <th></th>
                            :
                            console.log()
                        }
                        {puntoFinal.P13S !== '""' || puntoFinal.P23S !== '""' ?
                            <th></th>
                            :
                            console.log()
                        }
                        {puntuacion !== '""' ?
                            <th className="puntuacion">{puntuacion}</th> :
                            <th className="p">P</th>
                        }
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <th className="">{pareja1.replace(/['"]+/g, '')}</th>
                        <th>{puntoFinal.SAQUE1.replace(/['"]+/g, '')}</th>
                        <th>{puntoFinal.P11S.replace(/['"]+/g, '')}</th>
                        {puntoFinal.P12S !== '""' || puntoFinal.P22S !== '""' ?
                            <th>{puntoFinal.P12S.replace(/['"]+/g, '')}</th>
                            :
                            console.log()
                        }
                        {puntoFinal.P13S !== '""' || puntoFinal.P23S !== '""' ?
                            <th>{puntoFinal.P13S.replace(/['"]+/g, '')}</th>
                            :
                            console.log()
                        }
                        {puntoFinal.P12S !== '0' || puntoFinal.P2PS !== '0' ?
                            <th>{puntoFinal.P2PS.replace(/['"]+/g, '')}</th>
                            :
                            console.log()
                        }
                    </tr>
                    <tr>
                        <th>{puntoFinal.PAREJA2.replace(/['"]+/g, '')}</th>
                        <th>{puntoFinal.SAQUE2.replace(/['"]+/g, '')}</th>
                        <th>{puntoFinal.P21S.replace(/['"]+/g, '')}</th>
                        {puntoFinal.P12S !== '""' || puntoFinal.P22S !== '""' ?
                            <th>{puntoFinal.P22S.replace(/['"]+/g, '')}</th>
                            :
                            console.log()
                        }
                        {puntoFinal.P13S !== '""' || puntoFinal.P23S !== '""' ?
                            <th>{puntoFinal.P23S.replace(/['"]+/g, '')}</th>
                            :
                            console.log()
                        }
                        {puntoFinal.P1PS !== '""' || puntoFinal.P2PS !== '""' ?
                            <th>{puntoFinal.P2PS.replace(/['"]+/g, '')}</th>
                            :
                            console.log()
                        }
                    </tr>
                </tbody>
            </table>
        </div>
    )
}

export default Contador;
