            // return(() => {
                //     console.log(punto.value)
                // })
            })

        // const db = firebase.Database();
        // const starCountRef = db.ref('PUNTUADOR_FINAL_SAN_REMO_ULTIMATE')
        // onValue(starCountRef, (snapshot) => {
        // const data = snapshot.val();
        // updateStarCount(postElement, data);
// });
    }, []);


    return (
        <div>
            {/* <h1>{this.data.PUNTUADOR_FINAL_SAN_REMO_ULTIMATE}</h1> */}
            <table>
                <thead>
                    <tr>
                        <th></th>
                        <th></th>
                        <th></th>
                        <th></th>
                        <th></th>
                        <th>{puntoFinal.PUNTUACION}</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <th>{puntoFinal.PAREJA1}</th>
                        <th>{puntoFinal.SAQUE1}</th>
                        <th>{puntoFinal.P11S}</th>
                        {puntoFinal.P12S !== "0" &&
                            <th>{puntoFinal.P12S}</th>
                        }
                        <th>{puntoFinal.P13S}</th>
                        <th>{puntoFinal.P1PS}</th>
                    </tr>
                    <tr>
                        <th>{puntoFinal.PAREJA2}</th>
                        <th>{puntoFinal.SAQUE2}</th>
                        <th>{puntoFinal.P21S}</th>
                        <th>{puntoFinal.P22S}</th>
                        <th>{puntoFinal.P23S}</th>
                        <th>{puntoFinal.P2PS}</th>
                    </tr>
                </tbody>
            </table>
        </div>
    )
}

export default Contador;
